import * as React from 'react'
import styledComponents from 'styled-components'

export interface Pop {}

const Pop = styledComponents<Pop, any>('span')`
  background-color: #2096f3;
  top: ${props => this.props.position.x || 100}
  width: 100px;
  padding: 5px;
  border-radius: 10px;
  color: white;
  position: absolute;
  left: 50%;
  bottom: 125%;
  margin-left: -40px;
  text-align: center;
  ::after {
    content: "";
    position: absolute;
    top: 100%;
    left: 50%;
    margin-left: -5px;
    border-width: 5px;
    border-style: solid;
    border-color: #2096f3 transparent transparent transparent;
  }
`

export default class ReactPop extends React.Component<ReactPopProps, {}> {
  componentDidMount() {
    const { element } = this.props;
    const elementPosition = document.getElementById(element).getClientRects();

    this.setState({ position: elementPosition[0] })
  }

  render() {
    const { element } = this.props

    return (
      <Container>
        <Element id="element1">
          testando se o popup aparece no elemento 1
        </Element>
        <Element id="element2">
          ou se aparece no elemento 2
        </Element>
        <Pop />
      </Container>
    )
  }
}try {
    // @ts-ignore
    Pop.displayName = "Pop";
    // @ts-ignore
    Pop.__docgenInfo = { "description": "", "displayName": "Pop", "props": {} };
    // @ts-ignore
    if (typeof STORYBOOK_REACT_CLASSES !== "undefined")
        // @ts-ignore
        STORYBOOK_REACT_CLASSES["src/Pop.tsx#Pop"] = { docgenInfo: Pop.__docgenInfo, name: "Pop", path: "src/Pop.tsx#Pop" };
}
catch (__react_docgen_typescript_loader_error) { }
